import React from 'react';
 import {useState,useEffect} from 'react'
import Axios from 'axios';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.bundle.min';
//import { ToastContainer, toast } from 'react-toastify';

import './app.css'


function App() {
  const [Name, setName] = useState("");
  const [Order,setOrder] = useState(0);
  const [TotalList, setTotalList] = useState([]);

useEffect(() => {
  Axios.get('http://localhost:3001/list')
  .then((res)=>{
    setTotalList(res.data)
  }).catch(()=>{
    console.log('err');
  })

}, []);


  const addFun=()=>{
    Axios.post('http://localhost:3001/insertItem',{
      name:Name,
      order:Order
    }).then(()=>{
      alert('success')
    }).catch(()=>{
      alert('ooopss')
    })
  }
const updateData=(id)=>{
  const newData=prompt('enter the new data') 
  Axios.put('http://localhost:3001/update')
}





return(
  <div className='App'>

    <div className='Login'>
      <div  className='loginForm' >
        <label>FoodName</label>
        <input 
          type="text" 
          className='foodLabel' 
          placeholder='Choose your food'
          onChange={(e)=>{
          setName(e.target.value)
          }}       
        />

        <label>How Many</label>
          <input 
            type="number" 
            className='day' 
            placeholder='order' 
            onChange={(e)=>{
            setOrder(e.target.value)
            }}
          />
          <br/>
        <button className='btn btn-success' onClick={addFun}>Add+</button>
      </div>    
    </div> 

<div className='showData'>

  {TotalList.map((data,index)=>{


    return(

      <div key={index} className="dataWrapper" >

        <div className='dataForm'>
          <div className='dataContainer'>
            <h6>Name: {data.name}</h6>
            <h6>Order: {data.order}</h6>          
          </div>

          <div className='dataButtonWrapper'>
            <button className='btn btn-primary' onClick={updateData}>Update</button>
            <button className='btn btn-danger'>delete</button>         

          </div>
        </div>



      </div>
    )

  })}

</div>        

  </div> 
  )
}
export default App;
