import React from 'react';
 import {useState,useEffect} from 'react'
import Axios from 'axios';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.bundle.min';
//import { ToastContainer, toast } from 'react-toastify';

import './app.css'


function App() {
  const [Name, setName] = useState("");
  const [Order,setOrder] = useState(0);
  const [TotalList, setTotalList] = useState([]);

useEffect(() => {
  Axios.get('http://localhost:3001/list')
  .then((res)=>{
    setTotalList(res.data)
    console.log(res.data)
  }).catch(()=>{
    console.log('err');
  })

}, []);


  const addFun=()=>{
    Axios.post('http://localhost:3001/insertItem',{
      name:Name,
      order:Order
    }).then(()=>{
      alert('success')
    }).catch(()=>{
      alert('ooopss')
    })
  }
const updateData=(id)=>{
  const newName=prompt('enter the new data') 

  Axios.put('http://localhost:3001/update',
  {newName:newName, id:id})
}

const deleteData=(id)=>{
  Axios.delete(`http://localhost:3001/delete/${id}`)
}



return(
  <div className='App'>

    <div className='Login'>
      <div  className='loginForm' >
        <label>FoodName</label>
        <input 
          type="text" 
          className='foodLabel' 
          placeholder='Choose your food'
          onChange={(e)=>{
          setName(e.target.value)
          }}       
        />

        <label>How Many</label>
          <input 
            type="number" 
            className='day' 
            placeholder='order' 
            onChange={(e)=>{
            setOrder(e.target.value)
            }}
          />
          <br/>
        <button className='btn btn-success' onClick={addFun}>Add+</button>
      </div>    
    </div> 

<div className='showData'>

  {TotalList.map((val,index)=>{


    return(

      <div  key={index} className="dataWrapper" >

        <div className='dataForm'>
          <div  className='dataContainer'>
            <h6>Name: {val.name}</h6>
            <h6>Order: {val.order}</h6>          
          </div>

          <div className='dataButtonWrapper'>
            <button className='btn btn-primary'  onClick={()=>{updateData(val._id)}}>Update</button>
            <button className='btn btn-danger' onClick={()=>{deleteData(val._id)}}>delete</button>         

          </div>
        </div>



      </div>
    )

  })}

</div> 
<div>
<div class="modal" tabindex="-1">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <p>Modal body text goes here.</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>



</div>       

  </div> 
)
}
export default App;
