import {useState,useEffect} from 'react'
import Axios from 'axios';

import './app.css'

function App() {
   const [foodName, setFood] = useState("");
   const [Days, setDay] = useState(0);
   const [foodList, setList]=useState([]) 
   const [updateName, setUpdate]=useState("")

   useEffect(()=>{
    Axios.get('http://localhost:3001/read').then((res)=>{
      setList(res.data)
    })
   },[])
   const addtoList=()=>{
   Axios.post("http://localhost:3001",{
    foodName:foodName,
    Days:Days

  })
}

  return (
    <div className="App">

    <h1>log in</h1>

    <label>FoodName</label>

    <input type="text" className='foodLabel' onChange={(e)=>{
      setFood(e.target.value)
    }} />
    <label>Days</label>
    <input type="string" className='day' onChange={(e)=>{
      setDay(e.target.value)
    }}/><br/>
    <button onClick={addtoList}>submit</button>

    <div className='read'>
    <h1>added list</h1>
    {foodList.map((res ,index)=>{
      return(
        <div key={index} className="food">

        <h2>{res.foodName}</h2>
        <h5>{res.Days} </h5>
        <input type='text' placeholder='update the name' />
        <button >update</button>
        <button >delete</button>
        </div>
      )
    })
    }

    </div>
    </div> 



  )
}

export default App;
