import {useState,useEffect} from 'react'
import Axios from 'axios';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.bundle.min';
//import { ToastContainer, toast } from 'react-toastify';

import './app.css'
import Update from './components/Update';

function App() {
   const [foodName, setFood] = useState("");
   const [updateName, setUpdate]=useState("")
   const [Days, setDay] = useState(0);
   const [foodList, setList]=useState([]) 


   useEffect(()=>{
    Axios.get('http://localhost:3001/read').then((res)=>{
      setList(res.data)
    })
   },[])
//function to add data 
const addtoList=()=>{
   Axios.post("http://localhost:3001",{
    foodName:foodName,
    Days:Days,

  })
}

//function for update
const updateList=(id)=>{
  Axios.put("http://localhost:3001/update",{
    id:id,
    updateName:updateName,


 })
} 
//function for delete
const deleteList=(id)=>{
  Axios.delete(`http://localhost:3001/delete/${id}`)
} 
  return (
    <div className="App">
      <h1>Login</h1>

        <label>FoodName</label>
         <input type="text" className='foodLabel' placeholder='Choose your food' onChange={(e)=>{
          setFood(e.target.value)
           }} />

        <label>Days</label>
          <input type="number" className='day' placeholder='how may days you eat' onChange={(e)=>{
          setDay(e.target.value)
           }}/><br/>
        <button className='btn btn-success' onClick={addtoList}>submit</button>

<div className='read'>
  <h1>Added List</h1>
    {foodList.map((res ,index)=>{      

          return(

        <div key={index} className="food">
          <table class="table">
            <thead>
              <tr>
                <th scope="col">Name</th>
                <th scope="col">Days</th>
                <th scope="col">UpdateName</th>
                <th scope="col"></th>
              </tr>
            </thead>
               <tbody>
                  <tr>
                    <th scope="row">{res.foodName}</th>
                    <td>{res.Days}</td>

                    <td><button onClick={()=>updateList(res._id)} className="btn btn-info" >update</button></td>
                  <td> <input type='text' placeholder='update the name'onChange={(e)=>{
                    setUpdate(e.target.value)
                    }} />
                 </td>
                    <td><button onClick={() => {
                      deleteList(res._id)
                      console.log('deleted')
                    }}  className="btn btn-danger">delete</button></td>                        
                  </tr>                      
                </tbody> 
          </table>

        </div>
        )
      })
    }

  </div>
<Update/>
</div> 



  )
}

export default App;
