import React from 'react'
import {useState,useEffect} from 'react'
import Axios from 'axios';
import './update.css'
import '../../node_modules/bootstrap/dist/js/bootstrap.bundle.min';

function Update({ButtonUpdate}) {
  const [updateName, setUpdate]=useState("")



  useEffect(()=>{
    Axios.get('http://localhost:3001/read').then((res)=>{
      setList(res.data)
    })
   },[])
  


  return (
    <div className='updateLabel'>
        <div className='Content'>
          <div className='buttonClose'>
            <button onClick={() => ButtonUpdate(false)} className='close btn btn-danger float-start'>X</button>
          </div>  
          <div className='title'>
            <h2>Are You Sure</h2>
          </div>
          <div className='body'>        
          
            <input type='text' placeholder='update the name' onChange={(e)=>{
               setUpdate(e.target.value)
              }} />
          </div>
          <div className='footer'>
            <button onClick={()=>ButtonUpdate(false)} className='close btn btn-danger float-between ' >Cancel</button>
            <button            
              className='close btn btn-success float-between '>Update</button>
          </div>
      </div>      
    </div> 
    
  )
}

export default Update
