import React from 'react';
 import {useState,useEffect} from 'react'
import Axios from 'axios';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/bootstrap/dist/js/bootstrap.bundle.min';
import {Modal, Button } from 'react-bootstrap'
//import { ToastContainer, toast } from 'react-toastify';

import './app.css'
import UpdatePopup from './components/UpdatePopup';



function App() {
  const [Name, setName] = useState("");
  const [Order,setOrder] = useState(0);
  const [TotalList, setTotalList] = useState([]);
  const [show, setshow] = useState(false);

useEffect(() => {
  Axios.get('http://localhost:3001/list')
  .then((res)=>{
    setTotalList(res.data)
    console.log(res.data)
  }).catch(()=>{
    console.log('err');
  })

}, []);


  const addFun=()=>{
    Axios.post('http://localhost:3001/insertItem',{
      name:Name,
      order:Order
    }).then(()=>{
      alert('success')
    }).catch(()=>{
      alert('ooopss')
    })
  }
const updateData=(id)=>{
  const newName=prompt('enter the new data') 

  Axios.put('http://localhost:3001/update',
  {newName:newName, id:id})
}

const deleteData=(id)=>{
  setshow(true)
  Axios.delete(`http://localhost:3001/delete/${id}`)
}
const handleClose=()=>{
  setshow(false)
}


return(

  <div className='App'>


    <div className='Login'>
      <div  className='loginForm' >
        <label>FoodName</label>
        <input 
          type="text" 
          className='foodLabel' 
          placeholder='Choose your food'
          onChange={(e)=>{
          setName(e.target.value)
          }}       
        />

        <label>How Many</label>
          <input 
            type="number" 
            className='day' 
            placeholder='order' 
            onChange={(e)=>{
            setOrder(e.target.value)
            }}
          />
          <br/>
        <button className='btn btn-success' onClick={addFun}>Add+</button>
      </div>    
    </div> 

<div className='showData'>

  {TotalList.map((val,index)=>{


    return(

      <div  key={index} className="dataWrapper" >

        <div className='dataForm'>
          <div  className='dataContainer'>
            <h6>Name: {val.name}</h6>
            <h6>Order: {val.order}</h6>          
          </div>

          <div className='dataButtonWrapper'>
            <button className='btn btn-primary'  onClick={()=>{updateData(val._id)}}>Update</button>
            <button className='btn btn-danger' onClick={()=>{deleteData(val._id)}}>delete</button>         

          </div>
        </div>


      <UpdatePopup/>
      </div>
    )

  })}

</div> 
<div>



<Modal show={show} onHide={handleClose}>
<Modal.Header closeButton>
  <Modal.Title>Are you sure</Modal.Title>
</Modal.Header>
<Modal.Body>your going to miss this</Modal.Body>
<Modal.Footer>
  <Button variant="secondary" onClick={handleClose}>
    Close
  </Button>
  <Button variant="primary" onClick={handleClose}>
    Delete
  </Button>
</Modal.Footer>
</Modal>





</div>       

  </div> 
)
}
export default App;
